// EditForm.tsx
import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, Typography } from '@mui/material';

interface EditFormProps {
  onEdit: (id: number, name: string, age: number) => void;
  currentData: { id: number; name: string; age: number } | null;
}

const UpdateArticle: React.FC<EditFormProps> = ({ onEdit, currentData }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState<number | string>('');

  // Remplir le formulaire avec les données actuelles lorsque currentData est défini
  useEffect(() => {
    if (currentData) {
      setName(currentData.name);
      setAge(currentData.age);
    }
  }, [currentData]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name && age && currentData) {
      onEdit(currentData.id, name, parseInt(age.toString(), 10)); // Appeler la fonction onEdit avec l'ID
    } else {
      alert('Please fill all fields');
    }
  };

  if (!currentData) return null; // Ne rien afficher si aucun élément n'est sélectionné pour modification

  return (
    <form onSubmit={handleSubmit}>
      <Typography variant="h6" gutterBottom>
        Modification article
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            variant="outlined"
            fullWidth
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Age"
            variant="outlined"
            fullWidth
            value={age}
            onChange={(e) => setAge(e.target.value)}
            type="number"
          />
        </Grid>
        <Grid item xs={12}>
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Edit
          </Button>
        </Grid>
      </Grid>
    </form>
  );
};

export default UpdateArticle;
