import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, Typography, Paper } from '@mui/material';
import { useParams } from 'react-router-dom';

interface InsertFormProps {
  onUpdate: (name: string, age: number) => void;
}

const NewArticle: React.FC<InsertFormProps> = ({ onUpdate }) => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState<number | string>('');
  const { id } = useParams(); // Récupère l'ID de l'article depuis l'URL

  // Utilisation de useEffect pour appeler l'API lorsque l'ID change
  useEffect(() => {
    if (id) {
      // Appel de l'API pour obtenir les détails de l'article
      fetch(`http://localhost:3000/article/${id}`)
        .then((response) => response.json())
        .then((data) => {
          // Remplir les champs avec les données reçues
          setName(data.nom_article); // Remplir le nom de l'article
          setQuantity(data.quantity); // Remplir la quantité de l'article
        })
        .catch((error) => console.error('Erreur :', error));
    }
  }, [id]); // Déclencher l'effet chaque fois que l'ID change

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name && quantity) {
      onUpdate(name, parseInt(quantity.toString(), 10)); // Appelle la fonction onInsert
      setName('');
      setQuantity('');
    } else {
      alert('Please fill all fields');
    }
  };

  return (
    <Grid
      container
      justifyContent="center" // Centrer le formulaire
      alignItems="center"
      style={{ minHeight: '100vh' }} // Prendre toute la hauteur de la page
    >
      <Grid item xs={12} sm={8} md={6} lg={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <form onSubmit={handleSubmit}>
            <Typography variant="h6" gutterBottom>
              Modification Article
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  label="Name"
                  variant="outlined"
                  fullWidth
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Quantity"
                  variant="outlined"
                  fullWidth
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  type="number"
                />
              </Grid>
              <Grid item xs={12}>
                <Button type="submit" variant="contained" color="primary" fullWidth>
                  Save
                </Button>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default NewArticle;
