import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import { Container } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function Login(props: { disableCustomTheme?: boolean }) {
  const [emailError, setEmailError] = React.useState(false);
  const [emailErrorMessage, setEmailErrorMessage] = React.useState('');
  const [passwordError, setPasswordError] = React.useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = React.useState('');
  
  const navigate = useNavigate();  // Déclarez useNavigate ici

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();  // Empêche l'envoi du formulaire

    if (validateInputs()) {
      const data = new FormData(event.currentTarget);
      console.log({
        email: data.get('email'),
        password: data.get('password'),
      });

      // Effectuez la redirection ici après validation des inputs
      navigate('/article');
    }
  };

  const validateInputs = () => {
    const email = document.getElementById('email') as HTMLInputElement;
    const password = document.getElementById('password') as HTMLInputElement;
  
    let isValid = true;
  
    // Validation de l'email
    if (!email.value || !/\S+@\S+\.\S+/.test(email.value)) {
      setEmailError(true);
      setEmailErrorMessage('Adresse Email Invalide.');
      isValid = false;
    } else {
      setEmailError(false);
      setEmailErrorMessage('');
    }
  
    // Validation du mot de passe
    if (!password.value) {
      setPasswordError(true);
      setPasswordErrorMessage('Le mot de passe ne peut pas être vide.');
      isValid = false;
    } else {
      // Vérification de la longueur minimale
      if (password.value.length < 8) {
        setPasswordError(true);
        setPasswordErrorMessage('Votre mot de passe doit contenir au moins 8 caractères.');
        isValid = false;
      }
      // Vérification de la lettre majuscule
      else if (!/[A-Z]/.test(password.value)) {
        setPasswordError(true);
        setPasswordErrorMessage('Votre mot de passe doit contenir au moins une lettre majuscule.');
        isValid = false;
      }
      // Vérification du caractère spécial
      else if (!/[!@#$%^&*(),.?":{}|<>]/.test(password.value)) {
        setPasswordError(true);
        setPasswordErrorMessage('Votre mot de passe doit contenir au moins un caractère spécial.');
        isValid = false;
      } else {
        setPasswordError(false);
        setPasswordErrorMessage('');
      }
    }
  
    return isValid;
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline enableColorScheme />
      <Box component="form" onSubmit={handleSubmit} noValidate>
        <Typography variant="h5">Identifiez-vous</Typography>
        <FormControl>
          <FormLabel htmlFor="email">Email</FormLabel>
          <TextField
            error={emailError}
            helperText={emailErrorMessage}
            id="email"
            type="email"
            name="email"
            autoComplete="email"
            required
            fullWidth
          />
        </FormControl>
        <FormControl>
          <FormLabel htmlFor="password">Mot de passe</FormLabel>
          <TextField
            error={passwordError}
            helperText={passwordErrorMessage}
            name="password"
            type="password"
            id="password"
            required
            fullWidth
          />
        </FormControl>
        <Button type="submit" variant="contained" fullWidth>
          Se connecter
        </Button>
      </Box>
    </Container>
  );
}
