import * as React from 'react';
import { useNavigate } from 'react-router-dom'; 
import { extendTheme, styled } from '@mui/material/styles';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { AppProvider, Navigation, Router } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { PageContainer } from '@toolpad/core/PageContainer';
import Grid from '@mui/material/Grid2';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Dialog, DialogContent, DialogActions, TextField, Typography } from '@mui/material';

const NAVIGATION: Navigation = [
    {
        segment: 'dashboard',
        title: 'Articles',
        icon: <DashboardIcon />,
    },
];

const demoTheme = extendTheme({
    colorSchemes: { light: true, dark: true },
    colorSchemeSelector: 'class',
    breakpoints: {
        values: {
            xs: 0,
            sm: 600,
            md: 600,
            lg: 1200,
            xl: 1536,
        },
    },
});

function useDemoRouter(initialPath: string): Router {
    const [pathname, setPathname] = React.useState(initialPath);

    const router = React.useMemo(() => {
        return {
            pathname,
            searchParams: new URLSearchParams(),
            navigate: (path: string | URL) => setPathname(String(path)),
        };
    }, [pathname]);

    return router;
}

const Skeleton = styled('div')<{ height: number }>(({ theme, height }) => ({
    backgroundColor: theme.palette.action.hover,
    borderRadius: theme.shape.borderRadius,
    height,
    content: '" "',
}));

const ArticleTable = () => {
    const navigate = useNavigate();

    const [articles, setArticles] = React.useState<any[]>([]);
    const [loading, setLoading] = React.useState<boolean>(true);
    const [error, setError] = React.useState<string>('');

    React.useEffect(() => {
        fetch('http://localhost:3000/article')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Recuperation échouer');
                }
                return response.json();
            })
            .then((data) => {
                setArticles(data);
                setLoading(false);
            })
            .catch((error) => {
                setError('Recuperation échouer : '+error);
                setLoading(false);
            });
        }, []);
        
        const handleDelete = (id: number) => {
            fetch('http://localhost:3000/article', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id_article: id }),
        })
        .then((response) => {
            if (!response.ok) {
                throw new Error('Suppression echouer');
            }
            return response.json();
        })
            .then((data) => {
                setArticles((prevArticles) => prevArticles.filter((article) => article.id !== id));
                window.location.reload();
            })
            .catch((error) => {
                console.error('Erreur deAPI', error);
            });
    };

    const handleEdit = (id_article: number) => {
        navigate(`/update-article/${id_article}`);
    };

    if (loading) {
        return <div>Chargement...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>#</TableCell>
                        <TableCell>Nom</TableCell>
                        <TableCell>Quantite</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {articles.map((row) => (
                        <TableRow key={row.id_article}>
                            <TableCell>{row.id_article}</TableCell>
                            <TableCell>{row.nom_article}</TableCell>
                            <TableCell>{row.quantity}</TableCell>
                            <TableCell>
                                <Button
                                    onClick={() => handleEdit(row.id_article)}
                                    variant="contained"
                                    color="primary"
                                    size="small"
                                    style={{ marginRight: 8 }}
                                    >
                                    Modifier
                                </Button>
                                <Button
                                    onClick={() => handleDelete(row.id_article)}
                                    variant="contained"
                                    color="secondary"
                                    size="small"
                                    >
                                    Supprimer
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default function Article(props: any) {
    const { window } = props;
    const navigate = useNavigate();
    const [openModal, setOpenModal] = React.useState(false); // état pour la modal
    const [name, setName] = React.useState('');
    const [quantity, setQuantity] = React.useState<number | string>('');
    const router = useDemoRouter('/dashboard');
    const demoWindow = window ? window() : undefined;

    const handleInsert = () => {
        fetch('http://localhost:3000/article', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                nom: name,
                quantite: quantity,
            }),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Ajout article echouer');
                }
                return response.json();
            })
            .then((data) => {
                setOpenModal(false);
                window.location.reload();
                setName(''); 
                setQuantity('');
                navigate("/article")
            })
            .catch((error) => {
                console.error('Erreur API', error);
            });
        };
        
    return (
        <AppProvider
            navigation={NAVIGATION}
            router={router}
            theme={demoTheme}
            window={demoWindow}
        >
            <DashboardLayout>
                <PageContainer>
                    <Button
                        onClick={() => setOpenModal(true)}
                        variant="contained"
                        color="primary"
                        size="small"
                        style={{ marginRight: 8 }}
                    >
                        Nouvelle Article
                    </Button>
                    <Button
                        onClick={() => {
                            sessionStorage.removeItem("id_utilisateur");
                            navigate('/');
                        }}
                        variant="contained"
                        color="primary"
                        size="small"
                        style={{ marginRight: 8 }}
                    >
                        Déconnecter
                    </Button>
                    <Grid container spacing={1}>
                        <Grid size={5} />
                        <Grid size={8}>
                            <ArticleTable />
                        </Grid>
                    </Grid>

                    {/* Modal pour ajout d'article */}
                    <Dialog open={openModal} onClose={() => setOpenModal(false)} fullWidth maxWidth="sm">
                        <DialogContent>
                            <Typography variant="h6" gutterBottom>
                                Nouveau article
                            </Typography>
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Nom de l'article"
                                onChange={(e) => setName(e.target.value)}
                                value={name}
                                style={{ marginBottom: 16 }}
                            />
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Quantité"
                                onChange={(e) => setQuantity(e.target.value)}
                                value={quantity}
                                type="number"
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={() => setOpenModal(false)} color="secondary">
                                Annuler
                            </Button>
                            <Button onClick={handleInsert} variant="contained" color="primary">
                                Ajouter
                            </Button>
                        </DialogActions>
                    </Dialog>
                </PageContainer>
            </DashboardLayout>
        </AppProvider>
    );
}
