import * as React from 'react';
import { useNavigate } from 'react-router-dom'; // Importer useNavigate au début du fichier
import { extendTheme, styled } from '@mui/material/styles';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { AppProvider, Navigation, Router } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { PageContainer } from '@toolpad/core/PageContainer';
import Grid from '@mui/material/Grid2';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

const NAVIGATION: Navigation = [
    {
        segment: 'dashboard',
        title: 'Articles',
        icon: <DashboardIcon />,
    },
];

const demoTheme = extendTheme({
    colorSchemes: { light: true, dark: true },
    colorSchemeSelector: 'class',
    breakpoints: {
        values: {
            xs: 0,
            sm: 600,
            md: 600,
            lg: 1200,
            xl: 1536,
        },
    },
});

function useDemoRouter(initialPath: string): Router {
    const [pathname, setPathname] = React.useState(initialPath);

    const router = React.useMemo(() => {
        return {
            pathname,
            searchParams: new URLSearchParams(),
            navigate: (path: string | URL) => setPathname(String(path)),
        };
    }, [pathname]);

    return router;
}

const Skeleton = styled('div')<{ height: number }>(({ theme, height }) => ({
    backgroundColor: theme.palette.action.hover,
    borderRadius: theme.shape.borderRadius,
    height,
    content: '" "',
}));

const data = [
    { id: 1, name: 'Alice', age: 24 },
    { id: 2, name: 'Bob', age: 30 },
    { id: 3, name: 'Charlie', age: 28 },
];

const MyTable = () => {
    const navigate = useNavigate(); // Utilisation de useNavigate dans MyTable

    // Fonction pour supprimer
    const handleDelete = (id: number) => {
        console.log(`Suppression de l'élément avec id ${id}`);
      
        // Appel API pour supprimer l'article
        fetch('http://localhost:3000/article', {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ id_article: id }),
        })
          .then((response) => {
            if (!response.ok) {
              throw new Error('Erreur lors de la suppression de l\'article');
            }
            return response.json();
          })
          .then((data) => {
            console.log('Article supprimé avec succès:', data);
            // Vous pouvez ajouter une logique ici pour mettre à jour l'UI, comme retirer l'article de la liste
          })
          .catch((error) => {
            console.error('Erreur API', error);
            // Vous pouvez afficher un message d'erreur ici si nécessaire
          });
      };
      

    // Fonction pour modifier (rediriger vers la page de mise à jour)
    const handleEdit = (id: number) => {
        console.log(`Modification de l'élément avec id ${id}`);
        navigate(`/update-article`); // Rediriger vers la page de mise à jour avec l'id
    };

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Name</TableCell>
                        <TableCell>Age</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {data.map((row) => (
                        <TableRow key={row.id}>
                            <TableCell>{row.id}</TableCell>
                            <TableCell>{row.name}</TableCell>
                            <TableCell>{row.age}</TableCell>
                            <TableCell>
                                {/* Boutons de modification et suppression */}
                                <Button
                                    onClick={() => handleEdit(row.id)} // Modification de l'article
                                    variant="contained"
                                    color="primary"
                                    size="small"
                                    style={{ marginRight: 8 }}
                                >
                                    Modifier
                                </Button>
                                <Button
                                    onClick={() => handleDelete(row.id)} // Suppression de l'article
                                    variant="contained"
                                    color="secondary"
                                    size="small"
                                >
                                    Supprimer
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default function Article(props: any) {
    const { window } = props;
    const navigate = useNavigate();

    const router = useDemoRouter('/dashboard');
    const demoWindow = window ? window() : undefined;

    // Fonction pour ajouter une nouvelle article
    const handleInsert = () => {
        console.log('insertion');
        navigate('/new-article'); // Redirection vers la page d'ajout
    };

    return (
        <AppProvider
            navigation={NAVIGATION}
            router={router}
            theme={demoTheme}
            window={demoWindow}
        >
            <DashboardLayout>
                <PageContainer>
                    <Button
                        onClick={() => handleInsert()} // Action du bouton pour ajouter un article
                        variant="contained"
                        color="primary"
                        size="small"
                        style={{ marginRight: 8 }}
                    >
                        Nouvelle Article
                    </Button>
                    <Grid container spacing={1}>
                        <Grid size={5} />
                        <Grid size={8}>
                            <MyTable />
                        </Grid>
                    </Grid>
                </PageContainer>
            </DashboardLayout>
        </AppProvider>
    );
}
