import React, { useState, useEffect } from 'react';
import {
    Button, TextField, Grid, Typography, Paper, Dialog, DialogContent, DialogActions, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ArticleTable = ({ onEdit }) => {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetch('http://localhost:3000/article')
            .then(response => {
                if (!response.ok) throw new Error('Échec de la récupération');
                return response.json();
            })
            .then(data => {
                setArticles(data);
                setLoading(false);
            })
            .catch(error => {
                setError('Échec de la récupération : ' + error);
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Chargement...</div>;
    if (error) return <div>{error}</div>;

    return (
        <TableContainer component={Paper}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>#</TableCell>
                        <TableCell>Nom</TableCell>
                        <TableCell>Quantité</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {articles.map(article => (
                        <TableRow key={article.id_article}>
                            <TableCell>{article.id_article}</TableCell>
                            <TableCell>{article.nom_article}</TableCell>
                            <TableCell>{article.quantity}</TableCell>
                            <TableCell>
                                <Button
                                    onClick={() => onEdit(article)}
                                    variant="contained"
                                    color="primary"
                                    size="small"
                                    style={{ marginRight: 8 }}
                                >
                                    Modifier
                                </Button>
                                <Button
                                    variant="contained"
                                    color="secondary"
                                    size="small"
                                    onClick={() => {/* Logique de suppression ici */}}
                                >
                                    Supprimer
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

const Article = () => {
    const [openEditModal, setOpenEditModal] = useState(false);
    const [selectedArticle, setSelectedArticle] = useState(null);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState('');
    const navigate = useNavigate();

    const handleEdit = (article) => {
        setSelectedArticle(article);
        setName(article.nom_article);
        setQuantity(article.quantity);
        setOpenEditModal(true);
    };

    const handleUpdate = () => {
        fetch('http://localhost:3000/article', {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id_article: selectedArticle.id_article,
                nom: name,
                quantite: quantity,
            }),
        })
            .then(response => {
                if (!response.ok) throw new Error('Erreur lors de la mise à jour');
                return response.json();
            })
            .then(() => {
                setOpenEditModal(false);
                window.location.reload();
            })
            .catch(error => console.error('Erreur API', error));
    };

    return (
        <div>
            <Button
                onClick={() => {
                    sessionStorage.removeItem("id_utilisateur");
                    navigate('/');
                }}
                variant="contained"
                color="primary"
                size="small"
                style={{ marginBottom: 8 }}
            >
                Déconnecter
            </Button>
            <ArticleTable onEdit={handleEdit} />

            {/* Modal pour modifier l'article */}
            <Dialog open={openEditModal} onClose={() => setOpenEditModal(false)} fullWidth maxWidth="sm">
                <DialogContent>
                    <Typography variant="h6" gutterBottom>
                        Modifier l'Article
                    </Typography>
                    <TextField
                        variant="outlined"
                        fullWidth
                        label="Nom de l'article"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        style={{ marginBottom: 16 }}
                    />
                    <TextField
                        variant="outlined"
                        fullWidth
                        label="Quantité"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                        type="number"
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenEditModal(false)} color="secondary">
                        Annuler
                    </Button>
                    <Button onClick={handleUpdate} variant="contained" color="primary">
                        Mettre à jour
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default Article;
