import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, Typography, Paper } from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom'; // Importez useNavigate pour la redirection

const NewArticle: React.FC = () => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState<number | string>('');
  const { id_article } = useParams(); // Récupère l'ID de l'article depuis l'URL
  const navigate = useNavigate(); // Pour effectuer la redirection après la mise à jour

  // Utilisation de useEffect pour appeler l'API lorsque l'ID change
  useEffect(() => {
    if (id_article) {
      // Appel de l'API pour obtenir les détails de l'article
      fetch(`http://localhost:3000/article/${id_article}`)
        .then((response) => response.json())
        .then((data) => {
          console.log(data);
          // Remplir les champs avec les données reçues
          setName(data[0].nom_article); // Remplir le nom de l'article
          setQuantity(data[0].quantity); // Remplir la quantité de l'article
        })
        .catch((error) => console.error('Erreur lors de la récupération des données :', error));
    }
  }, [id_article]); // Déclenche l'effet chaque fois que l'ID change

  const handleUpdate = (name: string, quantity: number, id_article: string) => {
    // Envoi de la requête PUT pour mettre à jour l'article
    fetch('http://localhost:3000/article', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        nom: name,
        quantite: quantity,
        id_article: parseInt(id_article),
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erreur lors de la mise à jour de l\'article');
        }
        return response.json();
      })
      .then((data) => {
        navigate('/article'); // Vous pouvez changer cette redirection selon vos besoins
      })
      .catch((error) => {
        console.error('Erreur API', error);
      });
  };

  // Fonction pour gérer la soumission du formulaire
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name && quantity && id_article) {
      // Appelle handleUpdate avec l'ID, nom et quantité
      handleUpdate(name, parseInt(quantity.toString(), 10), id_article);
      navigate('/article'); 
    } else {
      alert('Please fill all fields');
    }
  };

  return (
    <Grid
      container
      justifyContent="center" // Centrer le formulaire
      alignItems="center"
      style={{ minHeight: '100vh' }} // Prendre toute la hauteur de la page
    >
      <Grid item xs={12} sm={8} md={6} lg={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <form onSubmit={handleSubmit}>
            <Typography variant="h6" gutterBottom>
              Modifier l'Article
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  fullWidth
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  fullWidth
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  type="number"
                />
              </Grid>
              <Grid item xs={12}>
                <Button type="submit" variant="contained" color="primary" fullWidth>
                  Modifier
                </Button>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default NewArticle;
