import React, { useState } from 'react';
import { Button, TextField, Grid, Typography, Paper } from '@mui/material';

interface InsertFormProps {
  onInsert: (name: string, age: number) => void;
}

const NewArticle: React.FC<InsertFormProps> = ({ onInsert }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState<number | string>('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name && age) {
      onInsert(name, parseInt(age.toString(), 10)); // Appelle la fonction onInsert
      setName('');
      setAge('');
    } else {
      alert('Please fill all fields');
    }
  };

  return (
    <Grid
      container
      justifyContent="center" // Centrer le formulaire
      alignItems="center"
      style={{ minHeight: '100vh' }} // Prendre toute la hauteur de la page
    >
      <Grid item xs={12} sm={8} md={6} lg={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <form onSubmit={handleSubmit}>
            <Typography variant="h6" gutterBottom>
              Nouvelle Article
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  label="Name"
                  variant="outlined"
                  fullWidth
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Age"
                  variant="outlined"
                  fullWidth
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                  type="number"
                />
              </Grid>
              <Grid item xs={12}>
                <Button type="submit" variant="contained" color="primary" fullWidth>
                  Insert
                </Button>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default NewArticle;
