import * as React from 'react';
import { useNavigate } from 'react-router-dom'; 
import { extendTheme, styled } from '@mui/material/styles';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { AppProvider, Navigation, Router } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { PageContainer } from '@toolpad/core/PageContainer';
import Grid from '@mui/material/Grid2';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Dialog, DialogContent, DialogActions, TextField, Typography } from '@mui/material';

const NAVIGATION: Navigation = [
    {
        segment: 'dashboard',
        title: 'Articles',
        icon: <DashboardIcon />,
    },
];

const demoTheme = extendTheme({
    colorSchemes: { light: true, dark: true },
    colorSchemeSelector: 'class',
    breakpoints: {
        values: {
            xs: 0,
            sm: 600,
            md: 600,
            lg: 1200,
            xl: 1536,
        },
    },
});

function useDemoRouter(initialPath: string): Router {
    const [pathname, setPathname] = React.useState(initialPath);

    const router = React.useMemo(() => {
        return {
            pathname,
            searchParams: new URLSearchParams(),
            navigate: (path: string | URL) => setPathname(String(path)),
        };
    }, [pathname]);

    return router;
}

const ArticleTable = () => {
    const navigate = useNavigate();

    const [articles, setArticles] = React.useState<any[]>([]);
    const [loading, setLoading] = React.useState<boolean>(true);
    const [error, setError] = React.useState<string>('');
    const [openModal, setOpenModal] = React.useState(false);
    const [selectedArticle, setSelectedArticle] = React.useState<any>(null);
    const [name, setName] = React.useState('');
    const [quantity, setQuantity] = React.useState<number | string>('');

    React.useEffect(() => {
        fetch('http://localhost:3000/article')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Recuperation échouer');
                }
                return response.json();
            })
            .then((data) => {
                setArticles(data);
                setLoading(false);
            })
            .catch((error) => {
                setError('Recuperation échouer : '+error);
                setLoading(false);
            });
    }, []);

    const handleDelete = (id: number) => {
        fetch('http://localhost:3000/article', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id_article: id }),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Suppression échouée');
                }
                return response.json();
            })
            .then(() => {
                setArticles((prevArticles) => prevArticles.filter((article) => article.id_article !== id));
            })
            .catch((error) => {
                console.error('Erreur API', error);
            });
    };

    const handleEdit = (article: any) => {
        setSelectedArticle(article);
        setName(article.nom_article);
        setQuantity(article.quantity);
        setOpenModal(true);
    };

    const handleUpdate = () => {
        if (name && quantity) {
            fetch('http://localhost:3000/article', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id_article: selectedArticle.id_article,
                    nom_article: name,
                    quantity: quantity,
                }),
            })
                .then((response) => {
                    if (!response.ok) {
                        throw new Error('Erreur lors de la mise à jour de l\'article');
                    }
                    return response.json();
                })
                .then(() => {
                    setOpenModal(false);
                    setName('');
                    setQuantity('');
                    setArticles((prevArticles) =>
                        prevArticles.map((article) =>
                            article.id_article === selectedArticle.id_article
                                ? { ...article, nom_article: name, quantity }
                                : article
                        )
                    );
                })
                .catch((error) => {
                    console.error('Erreur API', error);
                });
        } else {
            alert('Veuillez remplir tous les champs');
        }
    };

    if (loading) {
        return <div>Chargement...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>#</TableCell>
                        <TableCell>Nom</TableCell>
                        <TableCell>Quantité</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {articles.map((row) => (
                        <TableRow key={row.id_article}>
                            <TableCell>{row.id_article}</TableCell>
                            <TableCell>{row.nom_article}</TableCell>
                            <TableCell>{row.quantity}</TableCell>
                            <TableCell>
                                <Button
                                    onClick={() => handleEdit(row)}
                                    variant="contained"
                                    color="primary"
                                    size="small"
                                    style={{ marginRight: 8 }}
                                >
                                    Modifier
                                </Button>
                                <Button
                                    onClick={() => handleDelete(row.id_article)}
                                    variant="contained"
                                    color="secondary"
                                    size="small"
                                >
                                    Supprimer
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default function Article(props: any) {
    const { window } = props;
    const navigate = useNavigate();
    const router = useDemoRouter('/dashboard');
    const demoWindow = window ? window() : undefined;

    const [openModal, setOpenModal] = React.useState(false);
    const [name, setName] = React.useState('');
    const [quantity, setQuantity] = React.useState<number | string>('');

    const handleInsert = () => {
        fetch('http://localhost:3000/article', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                nom_article: name,
                quantity: quantity,
            }),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Ajout de l\'article échoué');
                }
                return response.json();
            })
            .then(() => {
                setOpenModal(false);
                setName('');
                setQuantity('');
                window.location.reload();
            })
            .catch((error) => {
                console.error('Erreur API', error);
            });
    };

    return (
        <AppProvider
            navigation={NAVIGATION}
            router={router}
            theme={demoTheme}
            window={demoWindow}
        >
            <DashboardLayout>
                <PageContainer>
                    <Button
                        onClick={() => setOpenModal(true)}
                        variant="contained"
                        color="primary"
                        size="small"
                        style={{ marginRight: 8 }}
                    >
                        Nouvelle Article
                    </Button>
                    <Button
                        onClick={() => {
                            sessionStorage.removeItem("id_utilisateur");
                            navigate('/');
                        }}
                        variant="contained"
                        color="primary"
                        size="small"
                        style={{ marginRight: 8 }}
                    >
                        Déconnecter
                    </Button>
                    <Grid container spacing={1}>
                        <Grid size={5} />
                        <Grid size={8}>
                            <ArticleTable />
                        </Grid>
                    </Grid>

                    {/* Modal pour ajout d'article */}
                    <Dialog open={openModal} onClose={() => setOpenModal(false)} fullWidth maxWidth="sm">
                        <DialogContent>
                            <Typography variant="h6" gutterBottom>
                                Nouveau article
                            </Typography>
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Nom de l'article"
                                onChange={(e) => setName(e.target.value)}
                                value={name}
                                style={{ marginBottom: 16 }}
                            />
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Quantité"
                                onChange={(e) => setQuantity(e.target.value)}
                                value={quantity}
                                type="number"
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={() => setOpenModal(false)} color="secondary">
                                Annuler
                            </Button>
                            <Button onClick={handleInsert} variant="contained" color="primary">
                                Ajouter
                            </Button>
                        </DialogActions>
                    </Dialog>

                    {/* Modal pour modification d'article */}
                    <Dialog open={openModal && selectedArticle} onClose={() => setOpenModal(false)} fullWidth maxWidth="sm">
                        <DialogContent>
                            <Typography variant="h6" gutterBottom>
                                Modifier l'article
                            </Typography>
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Nom de l'article"
                                onChange={(e) => setName(e.target.value)}
                                value={name}
                                style={{ marginBottom: 16 }}
                            />
                            <TextField
                                variant="outlined"
                                fullWidth
                                label="Quantité"
                                onChange={(e) => setQuantity(e.target.value)}
                                value={quantity}
                                type="number"
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={() => setOpenModal(false)} color="secondary">
                                Annuler
                            </Button>
                            <Button onClick={handleUpdate} variant="contained" color="primary">
                                Modifier
                            </Button>
                        </DialogActions>
                    </Dialog>
                </PageContainer>
            </DashboardLayout>
        </AppProvider>
    );
}
